import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-perfil',
  templateUrl: './perfil.component.html',
  styleUrls: ['./perfil.component.css']
})
export class PerfilComponent implements OnInit {

  experience = [
    {
      position: 'Desarrollador Web Full-Stack',
      company: 'Empresa X',
      years: '2023 - Actualidad',
      description: 'Desarrollo de aplicaciones web con Angular y Node.js, mantenimiento de bases de datos y optimización de rendimiento.'
    },
    {
      position: 'Técnico de Redes',
      company: 'Compañía Y',
      years: '2021 - 2023',
      description: 'Montaje y mantenimiento de redes, configuración de servidores y soporte técnico para clientes.'
    }
  ];

  education = [
    {
      title: 'Técnico en Informática y Redes',
      institution: 'Instituto Z',
      year: '2021'
    },
    {
      title: 'Curso de Angular Avanzado',
      institution: 'Udemy',
      year: '2023'
    }
  ];

  projects = [
    {
      title: 'A2GymHub',
      description: 'Descripción detallada del proyecto 1. Aquí puedes poner los detalles técnicos y lo que has logrado.',
      image: 'assets/proyecto1.jpg'
    },
    {
      title: 'Sabor Mediterráneo',
      description: 'Descripción detallada del proyecto 2. Explica qué herramientas usaste y los retos que enfrentaste.',
      image: 'assets/proyecto2.jpg'
    },
    {
      title: 'TALICS',
      description: 'Descripción detallada del proyecto 3. Un proyecto en el que hayas aprendido algo importante.',
      image: 'assets/proyecto3.jpg'
    }
  ];

  constructor() { }

  ngOnInit(): void {
  }
}
