import { Component, HostListener } from '@angular/core';

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.css']
})
export class HeaderComponent {
  @HostListener('window:scroll', [])
  onWindowScroll() {
    const header = document.getElementById('header');
    if (header) {
      if (window.pageYOffset > 100) { // Cambia este valor a la cantidad de desplazamiento que desees
        header.classList.add('minimized');
      } else {
        header.classList.remove('minimized');
      }
    }
  }
}
<header class="header" id="header">
  <nav>
    <a routerLink="/home" class="nav-link">
      <mat-icon>home</mat-icon> <span>Home</span>
    </a>
    <a routerLink="/perfil" class="nav-link">
      <mat-icon>account_circle</mat-icon> <span>Perfil</span>
    </a>
    <a routerLink="/cv" class="nav-link">
      <mat-icon>description</mat-icon> <span>CV</span>
    </a>

    <a routerLink="/contacte" class="nav-link">
      <mat-icon>phone</mat-icon> <span>Contacto</span>
    </a>
  </nav>
</header>
