import { Component, HostListener } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {

  // Variable para controlar el estado del header
  headerShrink = false;

  // Escucha el evento de scroll
  @HostListener('window:scroll', ['$event'])
  onWindowScroll() {
    const scrollTop = window.pageYOffset || document.documentElement.scrollTop;

    if (scrollTop > 100) { // Cuando el usuario ha hecho scroll 100px o más
      this.headerShrink = true;  // Activa la reducción del header
    } else {
      this.headerShrink = false; // Vuelve al tamaño original
    }
  }
}
<app-header></app-header>
<router-outlet></router-outlet>
<app-footer></app-footer>
