import { Component, OnInit } from '@angular/core';
import * as pdfjsLib from 'pdfjs-dist';

@Component({
  selector: 'app-cv',
  templateUrl: './cv.component.html',
  styleUrls: ['./cv.component.css']
})
export class CvComponent implements OnInit {
  pdfSrc: string = 'assets/mi-cv.pdf';  // Ruta del archivo PDF

  ngOnInit(): void {
    this.loadPDF();
  }

  loadPDF(): void {
    const pdfPath = this.pdfSrc;
    const pdfContainer = document.getElementById('pdf-container');

    pdfjsLib.getDocument(pdfPath).promise.then((pdf) => {
      // Cargar la primera página del PDF
      pdf.getPage(1).then((page) => {
        const scale = 1.5; // Ajuste del zoom del PDF
        const viewport = page.getViewport({ scale });

        const canvas = document.createElement('canvas');
        const context = canvas.getContext('2d');
        canvas.height = viewport.height;
        canvas.width = viewport.width;

        // Renderiza la página del PDF en el canvas
        page.render({
          canvasContext: context,
          viewport: viewport
        });

        // Muestra el canvas en el contenedor
        pdfContainer?.appendChild(canvas);
      });
    });
  }
}
